import { html } from 'lit';

export function getAttachmentFileIcon(filetype: string) {
  switch (filetype) {
    case 'img':
      return IMGFileIcon;
    case 'jpg':
      return JPGFileIcon;
    case 'jpeg':
      return JPEGFileIcon;
    case 'png':
      return PNGFileIcon;
    case 'webp':
      return WEBPFileIcon;
    case 'tiff':
      return TIFFFileIcon;
    case 'gif':
      return GIFFileIcon;
    case 'svg':
      return SVGFileIcon;
    case 'eps':
      return EPSFileIcon;
    case 'pdf':
      return PDFFileIcon;
    case 'doc':
      return DOCFileIcon;
    case 'docx':
      return DOCXFileIcon;
    case 'txt':
      return TXTFileIcon;
    case 'csv':
      return CSVFileIcon;
    case 'xls':
      return XLSFileIcon;
    case 'xlsx':
      return XLSXFileIcon;
    case 'ppt':
      return PPTFileIcon;
    case 'pptx':
      return PPTXFileIcon;
    case 'fig':
      return FIGFileIcon;
    case 'ai':
      return AIFileIcon;
    case 'psd':
      return PSDFileIcon;
    case 'indd':
      return INDDFileIcon;
    case 'aep':
      return AEPFileIcon;
    case 'mp3':
      return MP3FileIcon;
    case 'wav':
      return WAVFileIcon;
    case 'mp4':
      return MP4FileIcon;
    case 'mpeg':
      return MPEGFileIcon;
    case 'avi':
      return AVIFileIcon;
    case 'mkv':
      return MKVFileIcon;
    case 'html':
      return HTMLFileIcon;
    case 'css':
      return CSSFileIcon;
    case 'rss':
      return RSSFileIcon;
    case 'sql':
      return SQLFileIcon;
    case 'js':
      return JSFileIcon;
    case 'json':
      return JSONFileIcon;
    case 'java':
      return JAVAFileIcon;
    case 'xml':
      return XMLFileIcon;
    case 'exe':
      return EXEFileIcon;
    case 'dmg':
      return DMGFileIcon;
    case 'zip':
      return ZIPFileIcon;
    case 'rar':
      return RARFileIcon;
    default:
      return UnknownFileIcon;
  }
}

